  var doc = org.jsoup.Jsoup.parse(result);
  list = doc.select('.manga-list li').toArray().map(li=>({mid:li.select('.manga-name').attr('href').match(/^.*\/(.*$)/)[1],name:li.select('.manga-name').text(),author:li.select('.manga-author').text(),logo:li.select('.manga-img img').attr('src'),lastup:li.select('.manga-update').text().match(/^.*更新:(.*$)/)[1]}))
}
else {
  list = JSON.parse(result).items
}
list

