@js:
var Magua = new JavaImporter();
Magua.importPackage(
Packages.java.lang,
Packages.java.security
);
with(Magua) {
  function strToMd5(str) {
    let md5 = MessageDigest.getInstance("MD5");
    let bytes = md5.digest(String(str).getBytes());
    let strbuffer = new StringBuilder();
    bytes.forEach(a => {
      let bt = a & 0xff;
      if (bt < 16) {
        strbuffer.append("0");
      }
      strbuffer.append(Integer.toHexString(bt));
    });
    return strbuffer.toString();
  }
  function getSign(str) {
    return strToMd5(str+"&key=!RWIVVvxxxDDZXZlo!").toUpperCase();
  }
}
