function f(r) {
  if (r.indexOf("http") != -1) {
    return r;
  }
  var ul = r.substr(0, 4);
  var ulb = r.substr(0, 7);
  if (ul == "2017" || ul == "2018") {
    return "http://fo.700mh.com/" + r;
  }
  if (ulb == "2019/01" || ulb == "2019/02" || ulb == "2019/03" || ulb == "2019/04" || ulb == "2019/05" || ulb == "2019/06" || ulb == "2019/07" || ulb == "2019/08" || ulb == "2018/09" || ulb == "2018/10") {
    return "http://fo.700mh.com/" + r;
  }
}
