@js:
var chaplist = new Array();
org.jsoup.Jsoup.parse(result).select(".comic-chapters").toArray().reverse().forEach((vol,i)=>{
    chaplist.push({
      _id: String.fromCharCode("A".charCodeAt()+i),
      _name: vol.select(".Title").text().replace("列表","").replace("章节","单话")
    });
    vol.select("ul a[href$=html]").toArray().forEach(a=>{
      chaplist.push({text: String(a).match(/<span>([^<]*)</)[1], href: a.attr("href")});
    });
})
