function get_page(host, pad) {
    function get_url(num) {
        // 请求链接返回状态码
        let url = host + String(num).padStart(pad, '0') + '.jpg'
        return java.fetch(url, {}).code()
    }

    news = 1,old = 0, c1 = 0, c2 = 0, count = 0
    while (true) {
        count++
        if (count >= 12){
            return '错误'
        }
        code = get_url(news)
        if (news === 1 && code === 404){
            return '错误'
        }
        if (code === 200 && c1 === 0) {
            old = news
            news = news + 100
        } else if (code === 404) {
            if (get_url(news - 1) === 200) {
                s = news - 1
                break
            }
            c1 = Math.abs(Math.floor((news - old) / 2))
            c2 = news - old - c1 // 减去c1剩余的
            news = news - c1
        }

        if (code === 200 && c1 !== 0) {
            if (get_url(news + 1) !== 200) {
                s = news
                break
            }
            c1 = Math.abs(Math.floor(c2 / 2))
            c2 = c2 - c1
           old = news
            news = news + c1
        }
    }

    let list = []
    for (let i=1; i<=s; i++){
        list.push(host + String(i).padStart(pad, '0') + '.jpg')
    }
    return list
}

if (baseUrl.match(/baidu/)){
    [b, host, num, name, time_up, time] = baseUrl.split('##')
    if (num === '1' && name == '全一卷'){
        // 卷
        get_page(host, 3)
    } else {
        // 话
        result = get_page(host  + String(num).padStart(2, '0') + '/' , 2)
        if (result == '错误'){
            result = get_page(host  + String(num).padStart(2, '0') + '/' , 3)
            if (result == '错误'){
                time_list = String(time).split(/\//)
                if (time_list[1] == '12'){
                    host = host.replace(time, String(Number(time_list[0]) + 1) + '/01')
                } else {
                    t = time.match(/^\d+\//)[0] + String(Number(time.match(/\/(\d+)/)[1])+1).padStart(2, '0')
                    host = host.replace(time, t)
                }
                result = get_page(host+ String(num).padStart(2, '0') + '/', 2)
                if (result == "错误"){
                    host = host.replace(/202.+\/\d+\//, time_up)
                    get_page(host+ String(num).padStart(2, '0') + '/', 2)
                }
            }   
        }
    }

} else {
    list = []
    java.setContent('', result, '')
    page = parseInt(java.getString('select@tag.option.-1@text', false))
    html_list = java.getStringList('select@option@value', false).toArray().map(x=>{
        return baseUrl.replace(/\d+\.html/, x+'.html')
    })

    imgurl =  result.match(/imgurl = '(.*?)'/)[1]
    pad =imgurl.match(/(\d+)\./)[1].length
    num = parseInt(imgurl.match(/(\d+)\./)[1])
    if (pad <= 3){
        for (let i=num; i<=num+page-1; i++){
            list.push("http://web3.fm88web.top" + imgurl.replace(/\d+\./, String(i).padStart(pad, '0')+'.'))
        }
    } else {
        for (let i=0; i<html_list.length; i++){
            let url = "http://web3.fm88web.top" + java.aJax(html_list[i]).match(/imgurl = '(.*?)'/)[1]
            list.push(url)
        }
    }
    list

}

