@js:
var json = JSON.parse(result)
var host = json.data.imagedomain
var id = json.data.thumb.match(/\/(\d+)\./)[1]
var v = java.aJax('http://m.fumanhua2.com/manhua/'+ id +'/')
if (!v.match(/图书未经审核/)){
    let html = v
    result = org.jsoup.Jsoup.parse(html).select('.p1+div li a').toArray().map(x => {
        return {
            text: x.text(),
            href: 'http://m.fumanhua2.com' + String(x.attr('href')),
        }
    })
} else {
    result = json.data.comicList.map(x => {
        let time_up = x.updated_at.replace(/^(\d+)-(\d+).*/, '$1/$2/')
        let time = x.created_at.replace(/^(\d+)-(\d+).*/, '$1/$2')

        if (!time.match(/^202/)){
            time = json.data.created_at.replace(/^(\d+)-(\d+).*/, '$1/$2')
        }
        let url = '##' + host + time +'/' + id + '/##' + x.chapter + '##'  + x.name + '##' + time_up + '##'  + time
        return {
            text: x.name,
            href: 'http://www.baidu.com?t=' + url,
        }
    })
    result = result.reverse()
}
result

