@js:
/** Method 1 **
var Magua = new JavaImporter();
Magua.importPackage(
Packages.java.lang,
Packages.android.util,
Packages.java.security,
Packages.javax.crypto,
Packages.javax.crypto.spec
);
with(Magua) {
function decrypt(config, encryptedData) {
for (var k in config) {
  config[k] = String(config[k]);
}
var bm = config.bm;
var skey = new SecretKeySpec(config.pass.getBytes(bm), "AES");
var dcipher = Cipher.getInstance("AES/CBC/PKCS5Padding");
dcipher.init(Cipher.DECRYPT_MODE, skey, new IvParameterSpec(config.iv.getBytes(bm)));
var clearbyte = dcipher.doFinal(Base64.decode(encryptedData,Base64.NO_WRAP));
return new String(clearbyte, bm);
}
}

eval(result.match(/var chapterImages.*?chapterPath.*?;/)[0]);
var config = {
iv: "opb4x7z21vg1f3gI",
bm: "UTF-8",
pass: "cxNB23W8xzKJV26O"
}
var data = decrypt(config, chapterImages);

** Method 2 **/
eval(result.match(/var chapterImages.*?chapterPath.*?;/)[0]);
eval(""+java.aJax("https://m.acggd.com/js/crypto.js")+"");
var data = CryptoJS.AES.decrypt(chapterImages, CryptoJS.enc.Utf8.parse("cxNB23W8xzKJV26O"),{
"iv":CryptoJS.enc.Utf8.parse("opb4x7z21vg1f3gI"),
"mode":CryptoJS.mode.CBC,
"padding":CryptoJS.pad.Pkcs7
}).toString(CryptoJS.enc.Utf8);

var list = JSON.parse(data);
var piclist = new Array();
var host = java.aJax("https://m.acggd.com/js/config.js").match(/\["([^"]*)"\]/)[1];
for (let i in list) {
if (list[i].match(/^https?:\/\/(images.dmzj.com|imgsmall.dmzj.com)/i)) {
piclist[i] = host+"/showImage.php?url="+encodeURI(list[i]);
}
else if (list[i].match(/^[a-z]\//i)) {
piclist[i] = host+"/showImage.php?url="+encodeURI("https://images.dmzj.com/"+list[i]);
}
else if (list[i].match(/^(http:|https:|ftp:|^)\/\//i)) {
piclist[i] = list[i];
}
else {
piclist[i] = host+"/"+chapterPath+list[i];
}
}
