function nn(n) {
  return n < 10 ? '00' + n : n < 100 ? '0' + n : n;
}
function mm(p) {
  return (parseInt((p - 1) / 10) % 10) + (((p - 1) % 10) * 3)
}
function lc(l) {
  if (l.length != 2) return l;
  let az = 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ';
  let a = l.substring(0, 1);
  let b = l.substring(1, 2);
  if (a == 'Z')
    return 8000 + az.indexOf(b);
  else
    return az.indexOf(a) * 52 + az.indexOf(b);
}
function su(a, b, c) {
  let e = (a + '').substring(b, b + c);
  return (e);
}
function ge(s) {
  let a = s.substring(15);
  let b = eval(s.substring(0, 5));
  let c = eval(s.substring(5, 10));
  let d = eval(s.substring(10, 15));
  return 'http://img' + su(b, 0, 1) + '.8comic.com/' + su(b, 1, 1) + '/'  + ti + '/' + c + '/' + nn(a) + '_' + su(d, mm(a), 3) + '.jpg';
}

