function getImgs(str, next, page, count, arr) {
  let html = org.jsoup.Jsoup.parse(str);
  page++;
  if (str.match("服务器内部错误")) {
    arr.push(html.select("img").attr("src"));
    next = String(next).replace(/(-|_)\d+/,"$1"+page);
    count++;
    if (count>3) return arr;
  } else {
    arr.push(html.select("[src*=\"@get:{cid}\"]").attr("src"));
    next = html.select("mip-link:contains(下一页)").attr('href');
    count = 1;
  }
  if (next.match(/(-|_)\d+\.html/)) {
    str = java.fetch(next,{method:"get", headers:{"user-agent": "window"}}).body().string();
    return getImgs(str, next, page, count, arr);
  }
  return arr;
}
