var cid = result.match(/var CARTOON_ID = "(.*?)"/)[1];
var pid = result.match(/var CHAPTER_ID = "(.*?)"/)[1];
var num = result.match(/var TOTAL_PAGE = "(.*?)"/)[1];

var list = new Array(num);
for (let i = 0; i < num; i++) {
  let url = "https://www.setnmh.com" + decodeURI("@get:{url}").replace(/\d+-1-/,pid+"-"+(i+1)+"-");
  let key = java.aJax(url).match(/var KEY = "(.*?)"/)[1];
  let request_body = "key=" + key + "&cartoon_id=" +cid + "&chapter_id=" + pid + "&page=" + (i+1);
  let data = String(java.fetch("https://www.setnmh.com/comicseries/getpictrue.html", {
    method: "post",
    headers: {
      "x-requested-with": "XMLHttpRequest",
      "content-type": "application/x-www-form-urlencoded"
    },
    body: request_body
  }).body().string());
  list[i] = eval(data).current;
}
list

