list1 = [], list2 = [];
org.jsoup.Jsoup.parse(result).select(".links-of-books>li>a").toArray().forEach(a=>{
  var href = String(a.attr("href")).split("/");
  var text = String(a.text()).replace(/\(.*?p\)/i,"");
  if (href[2]=="1") {
    list1.push({href:href,text:text});
  }
  if (href[2]=="2") {
    list2.push({href:href,text:text});
  }
});
function Sort(A) {
  var B = [];
  var len = A.length;
  if ((len>1) && (parseInt(A[0].href[3]) > parseInt(A[1].href[3]))) {
    for (var i=len-1; i>=0; i--)
      B.push({href:A[i].href.join("/"), text:A[i].text});
  } else {
    for (var i=0; i<len; i++)
      B.push({href:A[i].href.join("/"), text:A[i].text});
  }
  return B;
}
Sort(list2).concat(Sort(list1))

