@js:
eval(result.match(/var chapterImages.*?chapterPath.*?;/)[0]);
var host = java.aJax("https://mh.dushimh.com/js/config.js").match(/\["[^"]*"\]/g)[0].slice(2,-2);
chapterImages.map(imgurl=>{
  if (imgurl.match(/^https?:\/\/(images.dmzj.com|imgsmall.dmzj.com)/i)) return host + "/showImage.php?url=" + encodeURI(imgurl);
  if (imgurl.match(/^[a-z]\//i)) return host + "/showImage.php?url=" + encodeURI("https://images.dmzj.com/" + imgurl);
  if (imgurl.match(/^(http:|https:|ftp:|^)\/\//i)) return imgurl;
  if (imgurl.match(/^(\/|images?)/i)) return host + "/" + imgurl.replace(/^\/*/,"");
  imgurl = chapterPath.replace(/^\/*|\/*$/g,"") + "/" + imgurl.replace(/^\/*/,"");
  return host + "/" + imgurl.replace(/^\/*/,"");
});
