ruleBookContent:
@js:
var chapters = JSON.parse(result).data.comic_chapter;
for (var i=0; i<chapters.length; i++) {
  if (String(chapters[i].chapter_id) == "@get:{chapter_id}") {
    var path = chapters[i].rule.split("$$");
    var host = "https://mhpic.manhualang.com";
    var pn = parseInt(chapters[i].end_num - chapters[i].start_num + 1);
    break;
  }
}
var piclist = new Array(pn);
for (var i=0; i<pn; i++) {
  piclist[i] = host+path[0]+(i+1)+path[1]+"-smh.high.webp";
}
piclist
