ruleBookContent:
@js:
eval(result.match(/var chapterImages.*?chapterPath.*?;/)[0]);
eval(result.match(/function getCirh\(\)\{.*\}/)[0]);
var host = getCirh();
chapterImages.map(filename=>{
  if (filename.match(/^https?:\/\/(images.dmzj.com|imgsmall.dmzj.com)/i)) return host + "/showImage.php?url=" + encodeURI(filename);
  if (filename.match(/^[a-z]\//i)) return host + "/showImage.php?url=" + encodeURI("https://images.dmzj.com/" + filename);
  if (filename.match(/^(http:|https:|ftp:|^)\/\//i)) return filename;
  if (filename.match(/^(\/|images?)/i)) return host + "/" + filename.replace(/^\/*/,"");
  filename = chapterPath.replace(/^\/*|\/*$/,"") + "/" + filename.replace(/^\/*/,"");
  return host + "/" + filename.replace(/^\/*/,"");
})

