}

var data = result.match(/img_data = "(.*?)"/)[1];
var num = result.match(/chapter_num="(.*?)"/)[1];
var type = result.match(/chapter-type="(.*?)"/)[1];
var domain = result.match(/chapter-domain="(.*?)"/)[1];
var key = result.match(/chapter-key="(.*?)"/)[1];
var arr = LZString.decompressFromBase64(data).split(",");
var time = Math.round(new Date()/1000);
arr.map(imgurl=>domain+"/uploads/"+imgurl+"?_MD="+MD5.md5(imgurl+key+time)+"?_TM="+time)
