ruleChapterList:
@js:
var content = org.jsoup.Jsoup.parse(result);
var h2list = content.select("#comic-book-list h2").toArray();
var chaplist = new Array();
content.select("#comic-book-list ol").toArray().forEach((ol,i)=>{
    chaplist.push({
      _id: String.fromCharCode("A".charCodeAt()+i),
      _name: h2list[i].text()
    });
    chaplist.push.apply(chaplist, ol.select("a").toArray().reverse());
});
chaplist

